package com.keda.webDemo.umcs.quartz;

import java.util.Calendar;
import java.util.Date;
import java.util.List;

import javax.annotation.Resource;

import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Service;

import com.keda.webDemo.umcs.constants.Constants;
import com.keda.webDemo.umcs.dao.RecivMsgDao;
import com.keda.webDemo.umcs.dao.SendMsgDao;
import com.keda.webDemo.umcs.dao.UserDao;
import com.keda.webDemo.umcs.dao.dto.RecivMsg;
import com.keda.webDemo.umcs.dao.dto.SendMsg;
import com.keda.webDemo.umcs.dao.dto.User;
import com.keda.webDemo.umcs.tools.FilesUtil;

@Service
public class AutoTimer {
	
	private final long LOGINTEVAL = 60 * 1000;
	
	private final int INTERVALDAYS = 7;
	
	@Resource
	private UserDao userDao;;
	@Resource
	private SendMsgDao sendMsgDao;
	@Resource
	private RecivMsgDao recivMsgDao;
		
	/**
	 * 如果客户端1分钟内没有请求过来，则自动登出
	 */
	@Scheduled(cron = "0 0/1 * * * ?")
	public void logout() {
		List<User> users = userDao.selectAll();
		
		for(User user : users) {
			if(Constants.LOGIN == user.getIsOnline()) {
				if(LOGINTEVAL < new Date().getTime() - user.getUpdateTime().getTime()) {
					user.setIsOnline(Constants.LOGOUT);
					user.setUpdateTime(new Date());
					userDao.update(user);
				}
			}
		}
	}
	
	/**
	 * 消息超过7天则自动删除
	 * 测试时每分钟执行一次"0 0/1 * * * ?"
	 * 每天执行一次即可"0 0 0 * * ?".
	 */
	@Scheduled(cron = "0 0 0 * * ?")
	public void clearMsg() {
		Date now = new Date();
		Calendar cNow = Calendar.getInstance();
		cNow.setTime(now);
		cNow.add(Calendar.DAY_OF_YEAR, -INTERVALDAYS);
		List<SendMsg> sMsgs = sendMsgDao.selectBySendTime(cNow.getTime());
		for(SendMsg sMsg : sMsgs) {
			if(Constants.ISTEXT != sMsg.getMsgType()) {
				FilesUtil.deleteFile(Constants.FILESAVEPATH + sMsg.getMsg());
			}
			deleteRecivMsg(sMsg.getId());
			sendMsgDao.delete(sMsg.getId());
		}
	}
	
	private void deleteRecivMsg(int sendMsgId) {
		List<RecivMsg> rMsgs = recivMsgDao.selectBySendMsgId(sendMsgId);
		for(RecivMsg rMsg : rMsgs) {
			recivMsgDao.delete(rMsg.getId());
		}
	}
	
	
}





